import React, { useState } from 'react';
import './App.css';

function App() {
  const [selected, setSelected] = useState('User experience');
  const [isOpen, setIsOpen] = useState(false);

  const options = [
    'User experience',
    'Developer experience',
    'Core protocol',
    'ZK L2',
    'Full roadmap'
  ];

  const gridItems = {
    'User experience': {
      columns: [
        [
          { name: 'Typescript SDK', progress: 75 }, // Added progress percentage
          'WebGPU proving'
        ],
        ['Wallet', 'Delegated transaction proving', 'Block explorer']
      ]
    },
    'Developer experience': {
      columns: [
        ['Box 1', 'Box 2', 'Box 3', 'Box 4'],
        ['Box 5', 'Box 6', 'Box 7', 'Box 8'],
        ['Box 0']
      ]
    },
    'Core protocol': {
      columns: [
        ['Protocol 1', 'Protocol 2', 'Protocol 3'],
        ['Protocol 4', 'Protocol 5', 'Protocol 6']
      ]
    },
    'ZK L2': {
      columns: [
        ['ZK Box 1', 'ZK Box 2', 'ZK Box 3'],
        ['ZK Box 4', 'ZK Box 5', 'ZK Box 6']
      ]
    },
    'Full roadmap': {
      columns: [
        ['Roadmap 1', 'Roadmap 2', 'Roadmap 3'],
        ['Roadmap 4', 'Roadmap 5', 'Roadmap 6']
      ]
    }
  };

  const renderBox = (item) => {
    if (typeof item === 'object' && item.progress !== undefined) {
      return (
        <div className="box progress-box">
          <div 
            className="progress-bar" 
            style={{ width: `${item.progress}%` }}
          ></div>
          <span className="box-content">{item.name}</span>
        </div>
      );
    }
    return <div className="box"><span>{item}</span></div>;
  };

  return (
    <div className="app">
      <div className="dropdown">
        <button 
          className="dropdown-button"
          onClick={() => setIsOpen(!isOpen)}
        >
          <div className="button-content">
            <span>{selected}</span>
            <svg 
              className={`chevron ${isOpen ? 'rotate' : ''}`}
              width="20" 
              height="20" 
              viewBox="0 0 20 20"
            >
              <path 
                d="M6 8l4 4 4-4"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
              />
            </svg>
          </div>
        </button>

        {isOpen && (
          <div className="dropdown-menu">
            {options.map(option => (
              <button
                key={option}
                className={`menu-item ${option === selected ? 'active' : ''}`}
                onClick={() => {
                  setSelected(option);
                  setIsOpen(false);
                }}
              >
                {option}
              </button>
            ))}
          </div>
        )}
      </div>

      <div className={`grid ${selected === 'Developer experience' ? 'three-columns' : ''}`}>
        {gridItems[selected].columns.map((column, columnIndex) => (
          <div key={columnIndex} className="column">
            {column.map((item, itemIndex) => (
              <div key={itemIndex}>
                {renderBox(item)}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
