import React, { useState, useRef, useEffect } from 'react';
import { ChevronDown, Sun, Moon } from 'lucide-react';
import './App.css';

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [selected, setSelected] = useState('User experience');
  const [arrows, setArrows] = useState([]);
  const [isDark, setIsDark] = useState(false);
  const boxRefs = useRef([]);

  const options = [
    'User experience',
    'Developer experience',
    'Core protocol',
    'ZK L2',
    'Full roadmap'
  ];

  const userExperienceBoxes = [
    'Typescript SDK',
    'Wallet',
    'WebGPU proving',
    'Delegated transaction proving'
  ];

  const devExperienceBoxes = {
    column1: [
      'Box 1',
      'Box 2',
      'Box 3',
      'Box 4',
      'Box 5',
      'Box 6',
      'Box 7',
      'Box 8',
      'Box 9'
    ],
    column2: [
      'Box 1',
      'Box 2',
      'Box 3',
      'Box 4',
      'Box 5',
      'Box 6'
    ]
  };

  const CoreProtocol = {
    column1: [
      'Box 1',
      'Box 2',
      'Box 3',
      'Box 4',
      'Box 5',
      'Box 6',
      'Box 7',
      'Box 8',
      'Box 9'
    ],
    column2: [
      'Box 1',
      'Box 2',
      'Box 3',
      'Box 4',
      'Box 5',
      'Box 6'
    ]
  };


  useEffect(() => {
    if (selected === 'User experience' && boxRefs.current.length === 4) {
      const calculateArrows = () => {
        const boxes = boxRefs.current.map(ref => ref.getBoundingClientRect());

        const arrow1 = {
          start: {
            x: boxes[0].right,
            y: boxes[0].top + boxes[0].height / 2
          },
          end: {
            x: boxes[1].left,
            y: boxes[1].top + boxes[1].height / 2
          }
        };

        const arrow2 = {
          start: {
            x: boxes[2].right,
            y: boxes[2].top + boxes[2].height / 2
          },
          end: {
            x: boxes[1].left,
            y: boxes[1].top + boxes[1].height / 2
          }
        };

        setArrows([arrow1, arrow2]);
      };

      calculateArrows();
      window.addEventListener('resize', calculateArrows);
      return () => window.removeEventListener('resize', calculateArrows);
    }
  }, [selected]);

  const renderArrows = () => {
    return arrows.map((arrow, index) => {
      const dx = arrow.end.x - arrow.start.x;
      const dy = arrow.end.y - arrow.start.y;
      const controlPointOffset = Math.abs(dx) * 0.5;

      return (
        <path
          key={index}
          d={`
            M ${arrow.start.x} ${arrow.start.y}
            C ${arrow.start.x + controlPointOffset} ${arrow.start.y},
              ${arrow.end.x - controlPointOffset} ${arrow.end.y},
              ${arrow.end.x} ${arrow.end.y}
          `}
          stroke={isDark ? "#6b7280" : "#94a3b8"}
          strokeWidth="2"
          fill="none"
          markerEnd={`url(#arrowhead-${isDark ? 'dark' : 'light'})`}
        />
      );
    });
  };

  const renderUserExperienceBoxes = () => {
    return (
      <div className="relative mt-8">
        <svg 
          className="absolute top-0 left-0 w-full h-full pointer-events-none"
          style={{ zIndex: 0 }}
        >
          <defs>
            <marker
              id="arrowhead-light"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon points="0 0, 10 3.5, 0 7" fill="#94a3b8" />
            </marker>
            <marker
              id="arrowhead-dark"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon points="0 0, 10 3.5, 0 7" fill="#6b7280" />
            </marker>
          </defs>
          {renderArrows()}
        </svg>
        <div className="grid-container">
          {userExperienceBoxes.map((label, index) => (
            <div
              key={index}
              ref={el => boxRefs.current[index] = el}
              className={`box ${isDark ? 'dark' : 'light'}`}
            >
              <span>{label}</span>
            </div>
          ))}
        </div>
      </div>
    );
  };

  const renderDevExperienceBoxes = () => {
    return (
      <div className="columns-container">
        {/* Column 1 */}
        <div className="column">
          <div className="column-grid">
            {devExperienceBoxes.column1.map((label, index) => (
              <div
                key={`col1-${index}`}
                className={`box ${isDark ? 'dark' : 'light'}`}
              >
                <span>{label}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Column 2 */}
        <div className="column">
          <div className="column-grid">
            {devExperienceBoxes.column2.map((label, index) => (
              <div
                key={`col2-${index}`}
                className={`box ${isDark ? 'dark' : 'light'}`}
              >
                <span>{label}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className={`app-container ${isDark ? 'dark' : 'light'}`}>
      <div className="header">
        <button
          onClick={() => setIsDark(!isDark)}
          className={`theme-toggle ${isDark ? 'dark' : 'light'}`}
        >
          {isDark ? <Sun size={24} /> : <Moon size={24} />}
        </button>
        <div className="dropdown-container">
          <button
            onClick={() => setIsOpen(!isOpen)}
            className={`dropdown-button ${isDark ? 'dark' : 'light'}`}
          >
            <span>{selected}</span>
            <ChevronDown
              className={`chevron ${isOpen ? 'rotate' : ''} ${isDark ? 'dark' : 'light'}`}
            />
          </button>

          {isOpen && (
            <div className={`dropdown-menu ${isDark ? 'dark' : 'light'}`}>
              {options.map((option) => (
                <button
                  key={option}
                  onClick={() => {
                    setSelected(option);
                    setIsOpen(false);
                  }}
                  className={`dropdown-item ${isDark ? 'dark' : 'light'}`}
                >
                  {option}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {selected === 'User experience' && renderUserExperienceBoxes()}
      {selected === 'Developer experience' && renderDevExperienceBoxes()}
    </div>
  );
};

export default App;
