import React, { useState } from 'react';
import { ArcherContainer, ArcherElement } from 'react-archer';
import './App.css';

function App() {
  const [selected, setSelected] = useState('User experience');
  const [isOpen, setIsOpen] = useState(false);

  const options = [
    'User experience',
    'Developer experience',
    'Core protocol',
    'ZK L2',
    'Full roadmap'
  ];

  const gridItems = {
    'User experience': {
      columns: [
        [
          { name: 'Typescript SDK', progress: 75 },
          { name: 'WebGPU proving', progress: 45 }
        ],
        [
          { name: 'Wallet', progress: 60 },
          { name: 'Delegated transaction proving', progress: 30 },
          { name: 'Block explorer', progress: 85 }
        ]
      ]
    },
    'Developer experience': {
      columns: [
        [
          { name: 'Box 1', progress: 40 },
          { name: 'Box 2', progress: 55 },
          { name: 'Box 3', progress: 70 },
          { name: 'Box 4', progress: 85 }
        ],
        [
          { name: 'Box 5', progress: 25 },
          { name: 'Box 6', progress: 90 },
          { name: 'Box 7', progress: 45 },
          { name: 'Box 8', progress: 60 }
        ],
        [
          { name: 'Box 0', progress: 50 }
        ]
      ]
    },
    'Core protocol': {
      columns: [
        [
          { name: 'Protocol 1', progress: 65 },
          { name: 'Protocol 2', progress: 40 },
          { name: 'Protocol 3', progress: 75 }
        ],
        [
          { name: 'Protocol 4', progress: 55 },
          { name: 'Protocol 5', progress: 80 },
          { name: 'Protocol 6', progress: 35 }
        ]
      ]
    },
    'ZK L2': {
      columns: [
        [
          { name: 'ZK Box 1', progress: 70 },
          { name: 'ZK Box 2', progress: 45 },
          { name: 'ZK Box 3', progress: 90 }
        ],
        [
          { name: 'ZK Box 4', progress: 55 },
          { name: 'ZK Box 5', progress: 30 },
          { name: 'ZK Box 6', progress: 85 }
        ]
      ]
    },
    'Full roadmap': {
      columns: [
        [
          { name: 'Roadmap 1', progress: 95 },
          { name: 'Roadmap 2', progress: 60 },
          { name: 'Roadmap 3', progress: 25 }
        ],
        [
          { name: 'Roadmap 4', progress: 40 },
          { name: 'Roadmap 5', progress: 75 },
          { name: 'Roadmap 6', progress: 50 }
        ]
      ]
    }
  };

  const renderBox = (item) => {
    return (
      <div className="box progress-box">
        <div 
          className="progress-bar" 
          style={{ width: `${item.progress}%` }}
        ></div>
        <span className="box-content">{item.name}</span>
      </div>
    );
  };

  return (
    <div className="app">
      <div className="dropdown">
        <button 
          className="dropdown-button"
          onClick={() => setIsOpen(!isOpen)}
        >
          <div className="button-content">
            <span>{selected}</span>
            <svg 
              className={`chevron ${isOpen ? 'rotate' : ''}`}
              width="20" 
              height="20" 
              viewBox="0 0 20 20"
            >
              <path 
                d="M6 8l4 4 4-4"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
              />
            </svg>
          </div>
        </button>

        {isOpen && (
          <div className="dropdown-menu">
            {options.map(option => (
              <button
                key={option}
                className={`menu-item ${option === selected ? 'active' : ''}`}
                onClick={() => {
                  setSelected(option);
                  setIsOpen(false);
                }}
              >
                {option}
              </button>
            ))}
          </div>
        )}
      </div>

      <div className={`grid ${selected === 'Developer experience' ? 'three-columns' : ''}`}>
        {gridItems[selected].columns.map((column, columnIndex) => (
          <div key={columnIndex} className="column">
            {column.map((item, itemIndex) => (
              <div key={itemIndex}>
                {renderBox(item)}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
