import React, { useState } from 'react';
import { ArcherContainer, ArcherElement } from 'react-archer';
import './App.css';

function App() {
  const [selected, setSelected] = useState('User experience');
  const [isOpen, setIsOpen] = useState(false);

  const options = [
    'User experience',
    'Developer experience',
    'Core protocol',
    'ZK L2',
    'Full roadmap'
  ];

  const renderBox = (item, relations = []) => {
    return (
      <ArcherElement
        id={item.name}
        relations={relations}
      >
        <div className="box progress-box">
          <div 
            className="progress-bar" 
            style={{ width: `${item.progress}%` }}
          ></div>
          <span className="box-content">{item.name}</span>
        </div>
      </ArcherElement>
    );
  };

  const gridItems = {
    'User experience': {
      columns: [
        [
          { 
            name: 'Typescript SDK', 
            progress: 75,
            relations: [{
              targetId: 'Wallet',
              targetAnchor: 'left',
              sourceAnchor: 'right',
              style: { 
                strokeColor: '#94a3b8',
                strokeWidth: 2,
                arrowLength: 8,
                arrowThickness: 1,
              }
            }]
          },
          { 
            name: 'WebGPU proving', 
            progress: 45,
            relations: [{
              targetId: 'Wallet',
              targetAnchor: 'left',
              sourceAnchor: 'right',
              style: { 
                strokeColor: '#cbd5e1',
                strokeWidth: 1,
                arrowLength: 5,
                arrowThickness: 1,
              }
            }]
          }
        ],
        [
          { name: 'Wallet', progress: 60 },
          { name: 'Delegated transaction proving', progress: 30 },
          { name: 'Block explorer', progress: 85 }
        ]
      ]
    },
    // ... rest of the gridItems remain the same
  };

  // ... rest of the component remains the same

  return (
    <div className="app">
      <div className="dropdown">
        {/* ... dropdown code remains the same ... */}
      </div>

      <ArcherContainer strokeColor="#cbd5e1">
        <div className={`grid ${selected === 'Developer experience' ? 'three-columns' : ''}`}>
          {gridItems[selected].columns.map((column, columnIndex) => (
            <div key={columnIndex} className="column">
              {column.map((item, itemIndex) => (
                <div key={itemIndex}>
                  {renderBox(item, item.relations || [])}
                </div>
              ))}
            </div>
          ))}
        </div>
      </ArcherContainer>
    </div>
  );
}

export default App;
