import React, { useState } from 'react';
import { ArcherContainer, ArcherElement } from 'react-archer';
import './App.css';

function App() {
  const [selected, setSelected] = useState('User experience');
  const [isOpen, setIsOpen] = useState(false);

  const options = [
    'User experience',
    'Developer experience',
    'Core protocol',
    'ZK L2',
    'Full roadmap'
  ];

  const renderBox = (item, relations = []) => {
    return (
      <ArcherElement
        id={item.name}
        relations={relations}
      >
        <div className="box progress-box">
          <div 
            className="progress-bar" 
            style={{ width: `${item.progress}%` }}
          ></div>
          <span className="box-content">{item.name}</span>
        </div>
      </ArcherElement>
    );
  };

  const gridItems = {
    'User experience': {
      columns: [
        [
          { 
            name: 'Typescript SDK', 
            progress: 75,
            relations: [{
              targetId: 'Wallet',
              targetAnchor: 'left',
              sourceAnchor: 'right',
              style: { 
                strokeColor: '#cbd5e1',
                strokeWidth: 1,
                arrowLength: 5,
                arrowThickness: 1,
              }
            }]
          },
          { name: 'WebGPU proving', progress: 45 }
        ],
        [
          { name: 'Wallet', progress: 60 },
          { name: 'Delegated transaction proving', progress: 30 },
          { name: 'Block explorer', progress: 85 }
        ]
      ]
    },
    // ... other gridItems remain the same
  };

  return (
    <div className="app">
      <div className="dropdown">
        <button 
          className="dropdown-button"
          onClick={() => setIsOpen(!isOpen)}
        >
          <div className="button-content">
            <span>{selected}</span>
            <svg 
              className={`chevron ${isOpen ? 'rotate' : ''}`}
              width="20" 
              height="20" 
              viewBox="0 0 20 20"
            >
              <path 
                d="M6 8l4 4 4-4"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
              />
            </svg>
          </div>
        </button>

        {isOpen && (
          <div className="dropdown-menu">
            {options.map(option => (
              <button
                key={option}
                className={`menu-item ${option === selected ? 'active' : ''}`}
                onClick={() => {
                  setSelected(option);
                  setIsOpen(false);
                }}
              >
                {option}
              </button>
            ))}
          </div>
        )}
      </div>

      <ArcherContainer strokeColor="#cbd5e1">
        <div className={`grid ${selected === 'Developer experience' ? 'three-columns' : ''}`}>
          {gridItems[selected].columns.map((column, columnIndex) => (
            <div key={columnIndex} className="column">
              {column.map((item, itemIndex) => (
                <div key={itemIndex}>
                  {renderBox(item, item.relations || [])}
                </div>
              ))}
            </div>
          ))}
        </div>
      </ArcherContainer>
    </div>
  );
}

export default App;
