import React, { useState } from 'react';
import './App.css';

function App() {
  const [selected, setSelected] = useState('User experience');
  const [isOpen, setIsOpen] = useState(false);

  const options = [
    'User experience',
    'Developer experience',
    'Core protocol',
    'ZK L2',
    'Full roadmap'
  ];

  return (
    <div className="app">
      <div className="dropdown">
        <button 
          className="dropdown-button"
          onClick={() => setIsOpen(!isOpen)}
        >
          {selected} â–¼
        </button>

        {isOpen && (
          <div className="dropdown-menu">
            {options.map(option => (
              <button
                key={option}
                className="menu-item"
                onClick={() => {
                  setSelected(option);
                  setIsOpen(false);
                }}
              >
                {option}
              </button>
            ))}
          </div>
        )}
      </div>

      {selected === 'User experience' && (
        <div className="grid">
          <div className="box">Typescript SDK</div>
          <div className="box">Wallet</div>
          <div className="box">WebGPU proving</div>
          <div className="box">Delegated transaction proving</div>
        </div>
      )}
    </div>
  );
}

export default App;
